$(document).ready(function(){
	/**
	 * 页面弹出div dialog
	 * @return {[type]} [description]
	 */
	$(".modal").click(function(){
        var a=$(this).attr('id');
        var w=$(a).width();
        var h=$(a).height();
        var ml='-'+Math.round(w/2);
        var mt='-'+Math.round(h/2);
        $(a).css({
            "margin-left":+ml,
            "width":+w,
            "height":+h,
            "margin-top":+mt
        });
        $(a).animate({
            "top":"40%",
            opacity: 'show'
        },200);
        $(a).fadeIn(200);
        $('.dialog-msk').fadeIn();
    });
    $(".cls,.dialog-msk").click(function(){
        var id=$(this).attr('id');
        $(id).fadeOut(100);
         $(id).animate({
            "top":"100%",
            opacity: 'hide'
        },200);
         $('.dialog-msk').fadeOut();
    });
    $(".dropdown").hover(function(){
        var id=$(this).data('id');
        $("#"+id).slideDown(200);
    })
    $("#user_menu").mouseleave(function(){
            $(this).slideUp(200);
    })
    /**
     * 滚动导航栏
     * @return {[type]} [description]
     */
    $(window).scroll(function(){
        var a = 300;
        if ($(window).scrollTop()>a) {
            //$(".nav").addClass('fixed');
            $('.backTop').fadeIn(300);
        }else{
            //$(".nav").removeClass('fixed');
            $('.backTop').fadeOut(200);
        }
    });
    $('.backTop').click(function(){
        $("body").scrollTop(0);
    });
    //首页推荐列表鼠标经过隐藏背景
    $('.recommend-list li').hover(function(){
        $(this).find('em,span').fadeOut(300);
    })
     $('.recommend-list li').mouseleave(function(){
        $(this).find('em,span').fadeIn(200);
    })
    //首页用户下拉菜单
    $('.user-block-dropdown').hover(function(){
        $('.user-block-menu').fadeIn(300);
        $(this).find('i').addClass('deg');
    })
     $('.user-block-menu').mouseleave(function(){
        $(this).fadeOut(200);
        $('.fa-chevron-down').removeClass('deg');
    })
     //未显示全部导航显示
     $('.nav-more').hover(function(){
        $('.all_nav').slideDown(200);
     })
})